import React, {useEffect, useState} from "react";
import { AppBar, Avatar, Toolbar, Typography, Button } from "@material-ui/core";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { useDispatch } from "react-redux";

import projects from '../../images/projects.png';
import useStyles from './styles';


const Navbar = () => {
    const classes = useStyles();
    const [user, setUser] = useState(JSON.parse(localStorage.getItem('profile')));
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const location = useLocation();

    const logout = () => {
        dispatch({
            type: 'LOGOUT'
        });
        localStorage.clear();
        setUser(null);
        navigate('/');


    }
    useEffect(()=>{
        const token = user?.token;

        setUser(JSON.parse(localStorage.getItem('profile')));
    }, [location])


    return (
        <AppBar className = {classes.appBar} position = "static" color = "inherit">
            <div className={classes.brandContainer}>
            <Typography className ={classes.heading} variant='h2' align='center'>
                    <Link to= "/"style={{ color: 'inherit', textDecoration: 'inherit'}}>PROJECTS</Link>
                    </Typography> 
                    <img className= {classes.image}src = {projects} alt = "projects" height = "60" />
            </div>
            <Toolbar className={classes.toolbar}>
            {user? (
                <div className= {classes.profile}>
                <Avatar className={classes.purple} alt = {user.result.name} src ={user.result.imageUrl}>
                {user.result.name.charAt(0)}
                </Avatar>
                <Typography className = {classes.userName} variant="h6">
                    {user.result.name}
                </Typography>
                <Button variant = "contained" className={classes.logout} color ="secondary" onClick = {logout} >
                Logout
                </Button>
                </div>
            ) : (
                <Link to= "/auth" style={{ color: 'inherit', textDecoration: 'inherit'}}><Button  variant = "contained" color="primary">
                Sign in
                </Button></Link>
            )}
            </Toolbar>  
         </AppBar>
  );
}

export default Navbar;
